//This class is only used to access the Official Digimon Website (Digimon.net) wich will be the only reference to this API.
//This class will be used to populate the database. So it will not be used in the current API!!
//After some tests, to assure it is working well, a automatic task will be created to populated the database using this class functions.
using HtmlAgilityPack;

namespace DigimonAPI.services;
public static class DS //Stands for DataBase seeder
{
	private static string baseUrl = "https://digimon.net/reference_en/detail.php?directory_name=";

	//Main function, get a html and try to get the dinamic elementos from it. This works only for the Digimon.net format.
	public static async Task<Digimon?> ParseDigimon(int index)
	{
		try
		{
			if (index < 0 || index >= directoryNames.Length)
			{
				throw new ArgumentException("Invalid index!");
			}
			string? html = await HP.GetHTML(baseUrl + directoryNames[index]);
			//Start HTML AGILITY PACK object, necessary for the HTML manipulation
			var htmlDoc = new HtmlDocument();
			htmlDoc.LoadHtml(html);
			//Getting digimon name
			string digimonName = htmlDoc.DocumentNode.SelectSingleNode("//h3[@class='c-titleSet__main c-titleSet__main--arrow']").InnerText.Trim();
			//Getting digimon description
			string digimonDescription = htmlDoc.DocumentNode
				.SelectSingleNode("//section[@class='p-ref__profile u-mgn']")
				.InnerText.Trim();
			//Removing the word "Profile" from description
			if (digimonDescription.StartsWith("Profile"))
			{
				digimonDescription = digimonDescription.Substring("Profile".Length).Trim();
			}
			//Getting digimon Tier - on the website, it is called Level
			string digimonTier = htmlDoc.DocumentNode
				.SelectSingleNode("//dt[text()='Level']/following-sibling::dd")
				.InnerText.Trim();
			//Getting digimon Type
			string digimonType = htmlDoc.DocumentNode
				.SelectSingleNode("//dt[text()='Type']/following-sibling::dd")
				.InnerText.Trim();
			//Getting digimon Attribute
			string digimonAttribute = htmlDoc.DocumentNode
				.SelectSingleNode("//dt[text()='Attribute']/following-sibling::dd")
				.InnerText.Trim();
			//Using internal function to get moves
			List<string> digimonSpecialMoves = GetSpecialMoves(htmlDoc);
			//The next steps will turn the strings in current objects
			Tier dTier = new Tier(digimonTier);
			entities.Type dType = new entities.Type(digimonType); //The namespace is necessary because Type is also a class in the native dotnet ecosystem --''
			entities.Attribute dAttribute = new entities.Attribute(digimonAttribute);
			ICollection<SpecialMove> sMoves = new HashSet<SpecialMove>();
			foreach(string sMove in digimonSpecialMoves)
			{
				sMoves.Add(new SpecialMove(sMove));
			}
			return new Digimon(digimonName, digimonDescription, directoryNames[index], dTier, dType, dAttribute, sMoves);
		} catch (Exception ex)
		{
			Console.WriteLine("[ERROR]Parse Digimon: " + ex.Message);
			return null; //The null return will be treated by the requested system
		}
	}
	//This function removes the iteration responsability from the main function.
	private static List<string> GetSpecialMoves(HtmlDocument htmlDoc)
	{
		var moves = new List<string>();
		try
		{
			//Getting node from the HTML
			var specialMovesNode = htmlDoc.DocumentNode
			.SelectSingleNode("//dt[text()='Special Move']/following-sibling::dd");
			//Starting empty list
			//Captured moves validation
			if(specialMovesNode != null)
			{
				//Split the captured moves by tag and dot.
				var movesArray = specialMovesNode.InnerHtml.Split(new[] { "<br>" }, StringSplitOptions.RemoveEmptyEntries);
				foreach (var move in movesArray)
				{
					moves.Add(move.Replace("・", "").Trim());
				}
			} else
			{
				throw new Exception("Unable to get Special Moves from Digimon!");
			}
			return moves;
		} catch (Exception ex)
		{
			Console.WriteLine("[ERROR]Parse Digimon: " + ex.Message);
			return moves;
		}
	}
